pipeline {
    agent { label 'master' }
    stages {
        stage('tar'){
            steps {
                script {
                    library 'sysUtils@master'
                    env.BACKUP_NAME = "jenkins-${env.JENKINS_VERSION}-backup-${date.currentDate}"
